import { Pessoa } from './../core/model';
import { Injectable } from '@angular/core';
import { Http, Headers, URLSearchParams } from '@angular/http';

export class PessoaFiltro {
  nome: string;
  pagina = 0;
  itensPorPagina = 5;
}

@Injectable()
export class PessoaService {

  pessoasUrl = 'http://localhost:8080/pessoas';
  constructor(private http: Http) { }

  pesquisar(filtro: PessoaFiltro): Promise<any> {

    const params = new URLSearchParams();
    const headers = new Headers();
    // tslint:disable-next-line:max-line-length
    headers.append('Authorization', 'Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9..D5uC2NrsNHaz8QyY8jMBA5oTQgENk-QaIdH0PQmj1lk');


    params.set('page', filtro.pagina.toString());
    params.set('size', filtro.itensPorPagina.toString());

    if (filtro.nome) {
      params.set('nome', filtro.nome);
    }

    return this.http.get(`${this.pessoasUrl}?listar`, { headers, search: params })
      .toPromise()
      .then(response => {
        const responseJson = response.json();
        const pessoas = responseJson.content;

        const resultado = {
          pessoas,
          total: responseJson.totalElements

        };
        return resultado;
      });
  }

  listarTodas(): Promise<any> {

    const headers = new Headers();
    // tslint:disable-next-line:max-line-length
    headers.append('Authorization', 'Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9..D5uC2NrsNHaz8QyY8jMBA5oTQgENk-QaIdH0PQmj1lk');

    return this.http.get(`${this.pessoasUrl}?listar`, { headers })
      .toPromise()
      .then(response => response.json().content);
  }

  excluir(codigo: number): Promise<void> {
    const headers = new Headers();
    // tslint:disable-next-line:max-line-length
    headers.append('Authorization', 'Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9..D5uC2NrsNHaz8QyY8jMBA5oTQgENk-QaIdH0PQmj1lk');

    return this.http.delete(`${this.pessoasUrl}/${codigo}`, { headers })
      .toPromise().then(() => null);
  }

  alterarStatus(ativo: boolean, codigo: Number): Promise<void> {

    const headers = new Headers();
    // tslint:disable-next-line:max-line-length
    headers.append('Authorization', 'Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9..D5uC2NrsNHaz8QyY8jMBA5oTQgENk-QaIdH0PQmj1lk');
    headers.append('Content-Type', 'application/json');

    return this.http.put(`${this.pessoasUrl}/${codigo}/ativo`, ativo, { headers })
      .toPromise().then(() => null);
  }

  salvar(pessoa: Pessoa): Promise<Pessoa> {
    const params = new URLSearchParams();
    const headers = new Headers();
    // tslint:disable-next-line:max-line-length
    headers.append('Authorization', 'Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9..D5uC2NrsNHaz8QyY8jMBA5oTQgENk-QaIdH0PQmj1lk');
    headers.append('Content-Type', 'application/json');

    return this.http.post(`${this.pessoasUrl}/`, JSON.stringify(pessoa), { headers })
      .toPromise().then((response => response.json()));
  }

  editar(pessoa: Pessoa): Promise<Pessoa> {
    const params = new URLSearchParams();
    const headers = new Headers();
    // tslint:disable-next-line:max-line-length
    headers.append('Authorization', 'Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9..D5uC2NrsNHaz8QyY8jMBA5oTQgENk-QaIdH0PQmj1lk');
    headers.append('Content-Type', 'application/json');
    return this.http.put(`${this.pessoasUrl}/${pessoa.codigo}`, JSON.stringify(pessoa), { headers })
      .toPromise().then((response => {
        console.log(response.json());
        return response.json();
      }));
  }

  buscarPorCodigo(codigo: number): Promise<Pessoa> {

    const params = new URLSearchParams();
    const headers = new Headers();
    // tslint:disable-next-line:max-line-length
    headers.append('Authorization', 'Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9..D5uC2NrsNHaz8QyY8jMBA5oTQgENk-QaIdH0PQmj1lk');

    return this.http.get(`${this.pessoasUrl}/${codigo}`, { headers })
      .toPromise().then((response => response.json()));
  }

}
